;; **
;;; ### Calculating distance between cities
;;; 
;;; Straight-line distance/Euclidean distance is calculated using the x and y coordinates of a City.
;; **

;; @@
(defn distance [a b]
  (let [dist-x (- (:x a) (:x b))
        dist-y (- (:y a) (:y b))]
    (math/sqrt (+ (math/expt dist-x 2)
                  (math/expt dist-y 2)))))
