(defn rep-nearest-neighbour-tsp [cities & {:keys [samples-count] :or {count (count cities)}}] 
  (let [samples (sample-cities cities samples-count)]
    (shortest-tour 
      (for [origin samples]
        (nearest-neighbour-tsp cities :origin origin)))))
