;; **
;;; Uncrossing can be achived by reversing a segment
;; **

;; @@
(defn reverse-tour 
  "Reverse a tour from start to end inclusive"
  [tour start end]
  (into [] (concat (subvec tour 0 start) (into [] (rseq (subvec tour start (+ end 1)))) (subvec tour (+ end 1)))))

(reverse-tour [1 2 3 4] 0 1)
