;; **
;;; ### ... and a helper method
;;; 
;;; This will execute each algorithm, time it and plot the result of the algorithm.
;; **

;; @@
(defn execute [cities & algorithms]
  (for [algorithm algorithms]
    (let [start-time (. System (nanoTime))
          tour (algorithm cities)]      
      [(str (count cities) " cities") (str "distance: "(tour-length tour)) (str "time: " (/ (double (- (. System (nanoTime)) start-time)) 1000000000.0) " secs") (plot-tour tour)])))

