;; **
;;; ### Finding the shortest tour
;;; 
;;; > `min-key`: https://clojuredocs.org/clojure.core/min-key
;; **

;; @@
(defn shortest-tour [tours]
  (apply min-key tour-length tours))
;; @@
;; =>
;;; {"type":"html","content":"<span class='clj-var'>#&#x27;tsp-notes/shortest-tour</span>","value":"#'tsp-notes/shortest-tour"}
;; <=

;; @@
(let [cities (random-cities 4)
      tours (all-tours cities)
      plots (for [tour tours] [(plot-tour tour) (tour-length tour)])
      shortest (shortest-tour tours)]
  [plots "shortest" (plot-tour shortest) (tour-length shortest)])
