;; @@
(let [cities (random-cities 10)
      points (map city-to-point cities)
      city (rand-nth cities)
      nearest (nearest-neighbour city (clojure.set/difference (set cities) #{city}))]
  (plot/compose 
    (plot/list-plot points)
    (plot/list-plot [(city-to-point city)] :color "#FF0000")
    (plot/list-plot [(city-to-point nearest)] :color "#00FF00")))
