;; **
;;; We can do this _uncrossing_ on **all** of the subsegments of a tour, starting from the longer subsegments first (because they are likely to improve the result significantly)
;;; 
;;; > `memoize` is used to cache the result of a function since the subsegments for a set of `n` cities are always the same  
;;; > `memoize`: https://clojuredocs.org/clojure.core/memoize
;; **

;; @@
(defn subsegments 
  "Return [i, j] pairs representing tour[i:j] inclusive"
  [city-count]
  (for [sub-length (reverse (range 2 city-count))
        index (reverse (range (+ 1 (- city-count sub-length))))]
  [index (+ index (- sub-length 1))]))

(def m-subsegments (memoize subsegments))
;; @@
;; =>
;;; {"type":"html","content":"<span class='clj-var'>#&#x27;tsp-notes/m-subsegments</span>","value":"#'tsp-notes/m-subsegments"}
;; <=

;; @@
(defn can-improve? 
  "If reversing tour[start:end] inclusive is shorter, return the improved link along with a true flag"
  [tour start end]
  ;; given a tour [...A, B....C, D...]
  ;; reverse if [...A, C...B, D] is shorter
  (let [cities-count (count tour)
        A (nth tour (mod (+ cities-count (- start 1)) cities-count))
        B (nth tour start)
        C (nth tour end)
        D (nth tour (mod  (+ 1 end) cities-count))
        AB (distance A B)
        CD (distance C D)
        AB-CD (+ AB CD)
        AC (distance A C)
        BD (distance B D)
        AC-BD (+ AC BD)]
    (if (> AB-CD AC-BD)
      [true (reverse-tour tour start end)]
      [false tour])))
;; @@
;; =>
;;; {"type":"html","content":"<span class='clj-var'>#&#x27;tsp-notes/can-improve?</span>","value":"#'tsp-notes/can-improve?"}
