;; @@
(let [cities (random-cities 200)
      a (future (execute cities nearest-neighbour-tsp))
      b (future (execute cities improved-nearest-neighbour-tsp))
      c (future (execute cities rep-nearest-neighbour-tsp))
      d (future (execute cities improved-rep-nearest-neighbour-tsp))]
  [@a @b @c @d])
